import React, {useState} from 'react'
import {Container, createMuiTheme, Grid, Paper, useMediaQuery} from '@material-ui/core'
import Breadcrumbs from "@material-ui/core/Breadcrumbs";
import Link from "@material-ui/core/Link";

import Home from "./Components/Home";
import Skills from "./Components/Skills";
import Project from "./Components/Project";

import CssBaseline from "@material-ui/core/CssBaseline";
import ThemeProvider from "@material-ui/styles/ThemeProvider";

import './App.css';
import useStyles from "./Components/useStyles";

export default function App() {

    const styles = useStyles();
    const [currentComponent, setCurrentComponent] = useState(<Home style={styles}/>);

    function changeCurrentComponent(event) {
        switch (event.target.id) {
            case 'Home':
                setCurrentComponent(<Home style={styles.paper}/>);
                document.getElementById('Home').style.color = 'white';
                document.getElementById('Skills').style.color = 'inherit';
                document.getElementById('Project').style.color = 'inherit';
                break;

            case 'Skills':
                setCurrentComponent(<Skills style={styles.paper}/>);
                document.getElementById('Home').style.color = 'inherit';
                document.getElementById('Skills').style.color = 'white';
                document.getElementById('Project').style.color = 'inherit';
                break;

            case 'Project':
                setCurrentComponent(<Project style={styles.paper}/>);
                document.getElementById('Home').style.color = 'inherit';
                document.getElementById('Skills').style.color = 'inherit';
                document.getElementById('Project').style.color = 'white';
                break;

            default:
                setCurrentComponent(<Home style={styles.paper}/>);
                document.getElementById('Home').style.color = 'white';
                document.getElementById('Skills').style.color = 'inherit';
                document.getElementById('Project').style.color = 'inherit';
                break;
        }

    }

    const prefersDarkMode = useMediaQuery('(prefers-color-scheme: dark)');

    const theme = React.useMemo(
        () =>
            createMuiTheme({
                palette: {
                    type: prefersDarkMode ? 'dark' : 'light',
                },
            }),
        [prefersDarkMode],
    );


    return (
        <ThemeProvider theme={theme}>
            <CssBaseline/>
            <Container maxWidth={false}>
                <div className={styles.root}>
                        <Grid item xs={12}>
                            <h1>Présentation</h1>
                            <div className={classes.root}>
                                <Grid container spacing={3}>
                                    <Grid item xs={12} sm={6}>
                                        <Paper className={classes.paper}>Moi</Paper>
                                    </Grid>
                                    <Grid item xs={12} sm={6}>
                                        <Paper className={classes.paper}>Formations</Paper>
                                    </Grid>
                                    <br/>
                                    <Grid item xs={12} sm={6}>
                                        <Paper className={classes.paper}>Expériences</Paper>
                                    </Grid>
                                    <Grid item xs={12} sm={6}>
                                        <Paper className={classes.paper}>Compétences</Paper>
                                    </Grid>
                                    <Grid item xs={12} sm={12} md={12} xl={12} lg={12}>
                                        {/*<CarouselProject/>*/}
                                        <Project/>
                                    </Grid>
                                </Grid>
                            </div>
                        </Grid>
                </div>
            </Container>
        </ThemeProvider>
    );
}
