import React from 'react'

import Home from './Components/Home';
import Skills from './Components/Skills';
import Project from './Components/Project';

import './App.css';
import Breadcrumbs from "@material-ui/core/Breadcrumbs";
import {Button, Link} from "@material-ui/core";

class App extends React.Component {

    constructor(props, context) {
        super(props, context);
        this.state = {
            currentComponent: <Home/>,
        }

        this.changeCurrentComponent = this.changeCurrentComponent.bind(this);
    }

    changeCurrentComponent(event) {
        console.log(event.target.innerText);
        switch (event.target.innerText) {
            case 'Home':
                this.setState({currentComponent: <Home/>});
                break;

            case 'Skills':
                this.setState({currentComponent: <Skills/>});
                break;

            case 'Project':
                this.setState({currentComponent: <Project/>})
                break;

            default:
                this.setState({currentComponent: <Home/>});
                break;
        }
    }

    render() {
        return (
            <>
                <Breadcrumbs aria-label={"breadcrumb"}>
                    <Link color={"inherit"} children={'Home'} onClick={this.changeCurrentComponent}>
                        Home
                    </Link>
                    <Link color={"inherit"} onClick={this.changeCurrentComponent}>
                        Skills
                    </Link>
                    <Link color={"initial"} onClick={this.changeCurrentComponent}>
                        Project
                    </Link>
                </Breadcrumbs>
                {this.state.currentComponent}
            </>
        )
    }
}

export default App;
