import React from 'react'
import Breadcrumbs from "@material-ui/core/Breadcrumbs";
import {Container, Grid, Link} from "@material-ui/core";

import Home from './Components/Home';
import Skills from './Components/Skills';
import Project from './Components/Project';

import './App.css';
import styles from '@material-ui/core/styles/makeStyles';

class App extends React.Component {

    constructor(props, context) {
        super(props, context);
        this.state = {
            currentComponent: <Home/>,
            defaultColorPageLink: 'inherit',
            activeColorPageLink: 'initial',
        }

        this.changeCurrentComponent = this.changeCurrentComponent.bind(this);
    }

    changeCurrentComponent(event) {
        console.log(event);
        switch (event.target.innerText) {
            case 'Home':
                this.setState({currentComponent: <Home/>});

                break;

            case 'Skills':
                this.setState({currentComponent: <Skills/>});
                break;

            case 'Project':
                this.setState({currentComponent: <Project/>})
                break;

            default:
                this.setState({currentComponent: <Home/>});
                break;
        }
    }

    render() {

        const classes = styles((theme) => ({
            root: {
                flexGrow: 1,
            },
            paper: {
                padding: theme.spacing(2)
            }
        }))

        const useStyle = classes()

        return (
            <div className={useStyle.root}>
                <Grid container spacing={3}>
                    <Grid item xs={12}>
                    </Grid>
                </Grid>
            </div>
        /*<Container maxWidth={false}>

                <Breadcrumbs aria-label={"breadcrumb"}>
