import React, {useState} from 'react'
import {Container, createMuiTheme, Grid, Paper, useMediaQuery} from '@material-ui/core'
import Breadcrumbs from "@material-ui/core/Breadcrumbs";
import Link from "@material-ui/core/Link";

import Home from "./Components/Home";
import Skills from "./Components/Skills";
import Project from "./Components/Project";

import {makeStyles} from "@material-ui/core/styles";
import './App.css';

const useStyles = makeStyles((theme) => ({
    palette: {
        type: 'dark',
    },
    root: {
        flexGrow: 1,
    },
    paper: {
        padding: theme.spacing(2),
        textAlign: 'center',
        color: theme.palette.text.secondary,
    },
}));



export default function App() {
    const [currentComponent, setCurrentComponent] = useState(<Home/>);
    const styles = useStyles();

    function changeCurrentComponent(event) {
        switch (event.target.innerText) {
            case 'Home':
                setCurrentComponent(<Home/>);
                document.getElementById('Home').style.color = 'white';
                document.getElementById('Skills').style.color = 'gray';
                document.getElementById('Project').style.color = 'gray';
                break;

            case 'Skills':
                setCurrentComponent(<Skills/>);
                document.getElementById('Home').style.color = 'gray';
                document.getElementById('Skills').style.color = 'white';
                document.getElementById('Project').style.color = 'gray';
                break;

            case 'Project':
                setCurrentComponent(<Project/>);
                document.getElementById('Home').style.color = 'gray';
                document.getElementById('Skills').style.color = 'gray';
                document.getElementById('Project').style.color = 'white';
                break;

            default:
                setCurrentComponent(<Home/>);
                document.getElementById('Home').style.color = 'white';
                document.getElementById('Skills').style.color = 'gray';
                document.getElementById('Project').style.color = 'gray';
                break;
        }

    }

    const prefersDarkMode = useMediaQuery('(prefers-color-scheme: dark)');

    const theme = React.useMemo(
        () =>
            createMuiTheme({
                palette: {
                    type: prefersDarkMode ? 'dark' : 'light',
                },
            }),
        [prefersDarkMode],
    );


    return (
        <ThemeProvider>

        </ThemeProvider>
    );
}
