import React from 'react'
import Breadcrumbs from "@material-ui/core/Breadcrumbs";
import {Container, Link} from "@material-ui/core";

import Home from './Components/Home';
import Skills from './Components/Skills';
import Project from './Components/Project';

import './App.css';

class App extends React.Component {

    constructor(props, context) {
        super(props, context);
        this.state = {
            currentComponent: <Home/>,
            defaultColorPageLink: 'inherit',
            activeColorPageLink: 'initial',
        }

        this.changeCurrentComponent = this.changeCurrentComponent.bind(this);
    }

    changeCurrentComponent(event) {
        console.log(event);
        switch (event.target.innerText) {
            case 'Home':
                this.setState({currentComponent: <Home/>});

                break;

            case 'Skills':
                this.setState({currentComponent: <Skills/>});
                break;

            case 'Project':
                this.setState({currentComponent: <Project/>})
                break;

            default:
                this.setState({currentComponent: <Home/>});
                break;
        }
    }

    render() {
        return (
            <Container maxWidth={'sm'}>
                <Breadcrumbs aria-label={"breadcrumb"}>
                    <Link color={this.state.activeColorPageLink} onClick={this.changeCurrentComponent}>
                        Home
                    </Link>
                    <Link color={this.state.defaultColorPageLink} onClick={this.changeCurrentComponent}>
                        Skills
                    </Link>
                    <Link color={this.state.defaultColorPageLink} onClick={this.changeCurrentComponent}>
                        Project
                    </Link>
                </Breadcrumbs>
                {this.state.currentComponent}
            </Container>
        )
    }
}

export default App;
