import React, {useState} from 'react'
import {Container, Grid, Paper} from '@material-ui/core'
import Breadcrumbs from "@material-ui/core/Breadcrumbs";
import Link from "@material-ui/core/Link";

import Home from "./Components/Home";
import Skills from "./Components/Skills";
import Project from "./Components/Project";

import {makeStyles} from "@material-ui/core/styles";
import './App.css';

const useStyles = makeStyles((theme) => ({
    palette: {
        type: 'dark',
    },
    root: {
        flexGrow: 1,
    },
    paper: {
        padding: theme.spacing(2),
        textAlign: 'center',
        color: theme.palette.text.secondary,
    },
}));



export default function App() {
    const [currentComponent, setCurrentComponent] = useState(<Home/>);
    const styles = useStyles();

    function changeCurrentComponent(event) {
        switch (event.target.innerText) {
            case 'Home':
                setCurrentComponent(<Home/>);
                document.getElementById('Home').style.color = 'white';
                document.getElementById('Skills').style.color = 'gray';
                document.getElementById('Project').style.color = 'gray';
                break;

            case 'Skills':
                setCurrentComponent(<Skills/>);
                document.getElementById('Home').style.color = 'gray';
                document.getElementById('Skills').style.color = 'white';
                document.getElementById('Project').style.color = 'gray';
                break;

            case 'Project':
                setCurrentComponent(<Project/>);
                document.getElementById('Home').style.color = 'gray';
                document.getElementById('Skills').style.color = 'gray';
                document.getElementById('Project').style.color = 'white';
                break;

            default:
                setCurrentComponent(<Home/>);
                document.getElementById('Home').style.color = 'white';
                document.getElementById('Skills').style.color = 'gray';
                document.getElementById('Project').style.color = 'gray';
                break;
        }

    }
    return (
        <Container maxWidth={false} className={styles.palette}>
            <div className={styles.root}>
                <Grid container spacing={3}>
                    <Grid item xs={12}>
                        <Breadcrumbs aria-label={"breadcrumb"} aria-setsize={20} className={styles.paper}>
                            <Link color={'initial'} id={'Home'} onClick={changeCurrentComponent}>
                                Home
                            </Link>
                            <Link color={'textSecondary'} id={'Skills'} onClick={changeCurrentComponent}>
                                Skills
                            </Link>
                            <Link color={'inherit'} id={'Project'} onClick={changeCurrentComponent}>
                                Project
                            </Link>
                        </Breadcrumbs>
                    </Grid>
                    <Grid item xs={12}>
                        {currentComponent}
                    </Grid>
                    <Grid item xs={12} sm={6}>
                        <Paper className={styles.paper}>xs=12 sm=6</Paper>
                    </Grid>
                    <Grid item xs={12} sm={6}>
                        <Paper className={styles.paper}>xs=12 sm=6</Paper>
                    </Grid>
                    <Grid item xs={6} sm={3}>
                        <Paper className={styles.paper}>xs=6 sm=3</Paper>
                    </Grid>
                    <Grid item xs={6} sm={3}>
                        <Paper className={styles.paper}>xs=6 sm=3</Paper>
                    </Grid>
                    <Grid item xs={6} sm={3}>
                        <Paper className={styles.paper}>xs=6 sm=3</Paper>
                    </Grid>
                    <Grid item xs={6} sm={3}>
                        <Paper className={styles.paper}>xs=6 sm=3</Paper>
                    </Grid>
                </Grid>
            </div>
        </Container>
    );
}
